  const { transactions } = useContext(GlobalContext);

  return (
    <div>
      <span className="subtitle">Your Balance</span>
      <hr />
      <br />
      <span className="balance">PKR 32,887.00</span>
      <div className="card f-c">
        <div className="expense">
          <span>EXPENSE</span>
          <br />
          <span className="num">-12,433</span>
        </div>
        <div className="income">
          <span>INCOME</span>
          <br />
          <span className="num">+23,442</span>
        </div>
      </div>
      <br />
    </div>
  );
}
