import React, { useState } from "react";

export default function AddTransaction() {
  const [tranText, setTranText] = useState("");
  const [tranAmount, setTranAmount] = useState("");

  function addTransHandle(e: any) {
    e.preventDefault();
  }

  return (
    <div>
      <span className="subtitle">Make Transaction</span>
      <hr />
      <br />
      <form>
        <input
          className="f-r"
          type="number"
          placeholder="Amount"
          onChange={(e) => setTranAmount(e.target.value)}
          value={tranAmount}
        />
        <input
          type="text"
          placeholder="Text"
          onChange={(e) => setTranText(e.target.value)}
          value={tranText}
        />
        <button onClick={addTransHandle}>Add Transaction</button>
      </form>
    </div>
  );
}
